mapper.update:
    
        

            SELECT m.name,m.id,i.email,i.birth,i.job
            FROM member m, member_info i
            where m.mno = #{mno}
            AND m.mno = i.mno

        

        UPDATE member_info
        SET
        birth = #{birth},
        email = #{email},
        job = #{job}
        WHERE mno = #{mno}
